mapper.select:
    select 
    
    from o_userorders
    where UserId = #{userId,jdbcType=BIGINT} 
	
       and OrderType in #{item}
    
    order by OrderTime desc

select 
a.*
from o_userorders a left join o_orderaddress b on a.OrderAddressId=b.OrderAddressId 
where a.BranchUserId = #{branchUserId,jdbcType=BIGINT} 

   and a.OrderType=#{ordertype,jdbcType=INTEGER}

 
   and a.Status=#{status,jdbcType=INTEGER}


    and  (a.UserOrderId  LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) or b.Reciver  LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) or b.Phone LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) )

order by a.PayTime desc,a.OrderTime desc
