mapper.select:
    select DISTINCT a.* from p_myproductactivitycode a left join  p_myproducttempapply b  on a.TempId=b.TempId 
		where  a.Status!=3 and a.TempId=#{tempid,jdbcType=INTEGER} 
    
        and  a.Status=#{activestatus,jdbcType=INTEGER}
    
    
        and  (a.CodeNo  LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' )  or a.CartId  LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' )  or b.Mobilephone LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) )
    
    order by a.CreateTime desc
