mapper.select:
select a.* from ti_activityworks as a left join o_orderaddress as b on a.OrderAddressId=b.OrderAddressId where a.ActId = #{actid,jdbcType=BIGINT}
and a.Status = #{status,jdbcType=INTEGER}
and (((a.Mobiephone LIKE CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) OR a.Reciever LIKE CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ))) or ((b.Phone LIKE CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) OR b.Reciver LIKE CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) )))
order by a.CreateTime desc

mapper.update:
update ti_activityworks
set Status = #{toStatus,jdbcType=INTEGER}
where ActId = #{actId,jdbcType=INTEGER} and  OrderAddressId is null 

    and Status in  #{item}
