mapper.select:
select 

from ti_advertimgs
where ProductId = #{productid,jdbcType=BIGINT} and PromoterUserId=#{promoterid,jdbcType=BIGINT}

mapper.update:
update ti_advertimgs
set ProductId = #{productid,jdbcType=BIGINT},
  PromoterUserId = #{promoteruserid,jdbcType=BIGINT},
  AdvertImgJson = #{advertimgjson,jdbcType=VARCHAR},
  AdvertContent = #{advertcontent,jdbcType=VARCHAR}
where Id = #{id,jdbcType=BIGINT}
