mapper.select:
select 

from ti_groupactivity
where PromoterUserId = #{promoteruserid,jdbcType=BIGINT}

   and  Status = #{status,jdbcType=INTEGER}

 
   and  (GactId=#{keywords,jdbcType=VARCHAR} OR Title LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) OR Reciver LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ) OR Mobilephone LIKE  CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' ))

order by CreateTime desc
