mapper.select:
select 

from ti_productstyles
where  Status=1 and ProductId = #{productId,jdbcType=BIGINT}

mapper.insert:
insert into ti_productstyles (StyleId, ProductId, Price, 
  PromoterPrice, ImgCount, ProductType, 
  DefaultImg, Status, Description, 
  ImgJson, Width, Hight
  )
values (#{styleid,jdbcType=BIGINT}, #{productid,jdbcType=BIGINT}, #{price,jdbcType=DOUBLE}, 
  #{promoterprice,jdbcType=DOUBLE}, #{imgcount,jdbcType=INTEGER}, #{producttype,jdbcType=INTEGER}, 
  #{defaultimg,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{description,jdbcType=VARCHAR}, 
  #{imgjson,jdbcType=VARCHAR}, #{width,jdbcType=DOUBLE}, #{hight,jdbcType=DOUBLE}
  )
