mapper.select:
select 

from ti_promotersapply
where 1=1

    and  PromoterUserId = #{userId,jdbcType=BIGINT}


    and  AgentUserId = #{agentUserId,jdbcType=BIGINT}


   and  Status = #{status,jdbcType=INTEGER}

 
   and  (PromoterUserId=#{companyName,jdbcType=VARCHAR} 
   OR CompanyName LIKE  CONCAT('%', #{companyName,jdbcType=VARCHAR},'%' ) 
   OR  Contacts LIKE  CONCAT('%', #{companyName,jdbcType=VARCHAR},'%' )  
   OR  Mobilephone LIKE  CONCAT('%', #{companyName,jdbcType=VARCHAR},'%' ))

order by CreatTime desc
