mapper.select:
    select b.userId from ti_promoters a,u_accounts b 
    where a.promoterUserId=b.userId and b.availableAmount <= #{amount,jdbcType=DOUBLE}
    UNION ALL
	select b.userId from u_branches a,u_accounts b where a.branchUserId=b.userId  
	and b.availableAmount<= #{amount,jdbcType=DOUBLE}

mapper.insert:
insert into u_accounts (UserID, AvailableAmount, FreezeCashAmount
  )
values (#{userid,jdbcType=BIGINT}, #{availableamount,jdbcType=DOUBLE}, #{freezecashamount,jdbcType=DOUBLE}
  )

mapper.update:
update u_accounts
set AvailableAmount = #{availableamount,jdbcType=DOUBLE},
  FreezeCashAmount = #{freezecashamount,jdbcType=DOUBLE}
where UserID = #{userid,jdbcType=BIGINT}
