mapper.select:
select 

from u_accountslogs
where UserId = #{userId,jdbcType=BIGINT} 

   and OrderId like CONCAT('%', #{keywords,jdbcType=VARCHAR},'%' )
   

   and Type= #{accountLogType,jdbcType=INTEGER}

order by LogId desc

select a.*,b.TotalPrice as ordertotalprice from u_accountslogs a,o_payorder b 
where a.OrderId=b.PayId and  a.UserId = #{userId,jdbcType=BIGINT}
and a.Type= #{accountLogType,jdbcType=INTEGER}
order by a.LogId desc

 select sum(a.Amount) as ordertotalprice from u_accountslogs a,o_payorder b 
 where a.OrderId=b.PayId and  a.UserId = #{userId,jdbcType=BIGINT} 
 and a.Type= #{accountLogType,jdbcType=INTEGER}

mapper.insert:
insert into u_accountslogs (LogId, UserId, Type, 
  Amount, OrderId, CreateTime
  )
values (#{logid,jdbcType=BIGINT}, #{userid,jdbcType=BIGINT}, #{type,jdbcType=INTEGER}, 
  #{amount,jdbcType=DOUBLE}, #{orderid,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}
  )
